xquery.xq-expression:
		                  declare variable $procurado as node() external;
		                  let $fotoUrl := data($procurado//div[@class="imagem"]/a/img/@src)
                          let $fotoId := substring-after(substring-before(substring-after($fotoUrl,"?"),"&amp;"),"=")
		                  return
		                  		$fotoId
